const allItems = $input.all();
const lastItem = allItems[allItems.length - 1];

if (lastItem && Array.isArray(lastItem.json.messages)) {
  const messages = lastItem.json.messages;
  const count = messages.length;

  if (count === 0) return [{ json: { message: "" } }];

  const extractFirstLine = (text) => {
    if (!text) return "";
    return text.split('\n')[0].replace(/^Input from user:\s*/, '');
  };

  const trimEndNewline = (text) => {
    if (!text) return "";
    return text.replace(/\n+$/, '');
  };

  const selectedMessages = (count === 1) ? [messages[0]] : messages.slice(-1);

  const combinedMessage = selectedMessages.map((msg, idx) => {
    return `Message ${idx + 1}:\nhuman: ${extractFirstLine(msg.human)}\nai: ${trimEndNewline(msg.ai)}`;
  }).join('\n\n');

  return [{ json: { messages: combinedMessage } }];
}

return [{ json: { messages: "" } }];

const items = $input.all();
const fullText = items.map(item => item.json.text || "").join(" ");

// Cari item yang punya properti fileName
const fileNameItem = items.find(item => item.json.fileName);
const fileName = fileNameItem?.json.fileName || "unknown.pdf";

const chunkSize = 500;
const overlap = 100;
const chunks = [];

for (let i = 0; i < fullText.length; i += chunkSize - overlap) {
  const chunk = fullText.slice(i, i + chunkSize);
  chunks.push({
    json: {
      text: chunk,
      metadata: {
        source: "shared_pdf",
        filename: fileName,
        uploadedAt: new Date().toISOString()
      }
    }
  });
}

return chunks;

const now = Math.floor(Date.now() / 1000);
const exp = $json.payload.exp;
return [
  {
    json: {
      isExpired: now > exp,
    },
  },
];

